import {InfluxDB, FieldType} from 'influx'

let influx

export default class NodeMetrics {
    static connect(host = 'localhost', database = 'node_metrics') {
        if (!host) throw new Error('Host must be supplied')

        influx = new InfluxDB({
            host,
            database,
            schema: [
              {
                measurement: 'express_response_times',
                fields: {
                  duration: FieldType.INTEGER,
                },
                tags: [
                  'path',
                  'method',
                  'statusCode',
                  'requestBody'
                ]
              }
            ]
        })

        return influx.getDatabaseNames()
            .then(names => {
                if(names.includes(database)) {
                    return
                }
                return influx.createDatabase(database)
            })
            .then(() => true)
    }

    static expressMetricsMiddleware(exception, request, response, next) {
        const start = Date.now()
        const {path, method, body: requestBody} = request

        response.on('finish', () => {
            const duration = Date.now() - start
            console.log(response)
            influx.writePoints([
            {
                measurement: 'express_response_times',
                tags: { path, method, requestBody },
                fields: { duration },
            }
            ]).catch(err => {
                console.error(`Error saving data to InfluxDB! ${err.stack}`)
            })
        })
        return next()
    } 
}

